// api/graphql/Post.ts

import { schema } from 'nexus'

schema.objectType({
  name: 'Post',            // <- Name of your type
  definition(t) {
    t.int('id')            // <- Field named `id` of type `Int`
    t.string('title')      // <- Field named `title` of type `String`
    t.int('body')          // <- Field named `body` of type `String`
    t.boolean('published') // <- Field named `published` of type `Boolean`
  },
})

type Post {
  id: Int
  title: String
  body: String
  published: Boolean
}

// api/graphql/Post.ts     // 1
// ...

schema.extendType({
  type: 'Query',            // 2
  definition(t) {
    t.field('drafts', {      // 3
      type: 'Post',         // 4
      list: true,           // 5
    })
  },
})

import { schema } from 'nexus'

schema.extendType({
  type: 'Query',
  definition(t) {
    t.field('drafts', {
      type: 'Post',
      list: true,
      resolve() {
        return [{ id: 1, title: 'Nexus', body: '...', published: false }]
      },
    })
  },
})

